

var stuff_ = {};
var id_next = 0

var add = function(thing) {
	thing.thing_id = things.id_next;
	things.stuff_[things.id_next] = thing;
	things.id_next += 1;
}

var remove = function(id) {
	delete things.stuff_[id];
}

var get = function(id) {
	return stuff_[id]
}

exports.add = add;
exports.remove = remove;
exports.get = get;